import React, { useState } from "react";
import shopData from "./dummyData";

const Browse = () => {
  const [shopList, setshopList] = useState(shopData);

  const displayshopData = () => {
    if (shopList.length === 0)
      return <h1 className="text-center">No Data Found</h1>;

    return laptopList.map((laptop) => (
      <div className="col-md-3 mb-4">
        <div className="card">
          <img src={laptop.image} alt="" />
          <div className="card-body">
            <h4>{laptop.name}</h4>
            <p>{laptop.rating} avg rating </p>
            <p>{laptop.reviews} reviews</p>
            <h2>â‚¹{laptop.price}</h2>
          </div>
        </div>
      </div>
    ));
  };

  const filterLaptop = (e) => {
    const value = e.target.value;
    setLaptopList(
      shopData.filter((laptop) => {
        return laptop.title.toLowerCase().includes(value.toLowerCase());
      })
    );
  };

  return (
    <div>
      <header className="bg-body-tertiary">
        <div className="container py-5">
          <p className="display-2 text-center fw-bold">Shop List</p>
          <input
            type="text"
            className="form-control w-75 m-auto"
            onChange={filterLaptop}
          />
        </div>
      </header>
      <div className="container">
        <div className="row mt-5">{displayLaptopData()}</div>
      </div>
    </div>
  );
};

export default Browse;
