import { useFormik } from "formik";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";

const Signup = () => {

  const navigate = useNavigate();

  const [selImage, setSelImage] = useState('');

  const signupForm = useFormik({
    initialValues: {
      name: '',
      email: '',
      password: ''
    },

    onSubmit: async (values) => { 
      values.avatar = selImage;
      console.log(values);

      // sending request to backend
      const res = await fetch('http://localhost:5000/user/add', {
        method: 'POST',
        body : JSON.stringify(values),
        headers: {
          'Content-Type': 'application/json'
        }
      });

      console.log(res.status);

      if(res.status === 200){
        Swal.fire({
          icon : 'success',
          title : 'Signup Success!!',
          text: 'Now Login to Continue'
        });
        navigate('/login');
      }else{
        Swal.fire({
          icon : 'error',
          title : 'Oops!!',
          text: 'Some Error Occured'
        });
      }
    }
  })

  const uploadFile = async (e) => {
    let file = e.target.files[0];
    setSelImage(file.name);
    const fd = new FormData();
    fd.append('myfile', file);

    const res = await fetch('http://localhost:5000/util/uploadfile', {
      method: 'POST',
      body: fd
    });

    console.log(res.status);
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="card w-25 shadow-lg rounded-5">
        <div className="card-body p-5">
          <i className="fa-solid fa-lock fa-3x d-block text-center" />
          <h2 className="text-center my-5">Signup Form</h2>
          <form onSubmit={signupForm.handleSubmit}>
            <label htmlFor="">Name</label>
            <input className="form-control mb-4 rounded-5" type="text" name="name" onChange={signupForm.handleChange} value={signupForm.values.name} />
            <label htmlFor="">Email</label>
            <input className="form-control mb-4 rounded-5" type="email" name="email" onChange={signupForm.handleChange} value={signupForm.values.email} />
            <label htmlFor="">Password</label>
            <input className="form-control mb-4 rounded-5" type="password" name="password" onChange={signupForm.handleChange}  value={signupForm.values.password} />
            
            <label htmlFor="">Upload File</label>
            <input type="file" onChange={uploadFile} />

            <button className="btn btn-danger w-100 mt-4 rounded-5">
              Signup
            </button>
          </form>
        </div>
      </div>
      <section
className="vh-100 bg-image"
style={{
  backgroundImage:
    'url("https://mdbcdn.b-cdn.net/img/Photos/new-templates/search-box/img4.webp")'
}}
>
<div className="mask d-flex align-items-center h-100 gradient-custom-3">
  <div className="container h-100">
    <div className="row d-flex justify-content-center align-items-center h-100">
      <div className="col-12 col-md-9 col-lg-7 col-xl-6">
        <div className="card" style={{ borderRadius: 15 }}>
          <div className="card-body p-5">
            <h2 className="text-uppercase text-center mb-5">
              Create an account
            </h2>
            <form onSubmit={signupForm.handleSubmit}>
              <div className="form-outline mb-4">
                <input
                  type="text"
                  id="form3Example1cg"
                  className="form-control form-control-lg" onChange={signupForm.handleChange} value={signupForm.values.name}
                />
                <label className="form-label" htmlFor="form3Example1cg">
                  Your Name
                </label>
              </div>
              <div className="form-outline mb-4">
                <input
                  type="email"
                  id="form3Example3cg"
                  className="form-control form-control-lg" onChange={signupForm.handleChange} value={signupForm.values.email}
                />
                <label className="form-label" htmlFor="form3Example3cg">
                  Your Email
                </label>
              </div>
              <div className="form-outline mb-4">
                <input
                  type="password"
                  id="form3Example4cg"
                  className="form-control form-control-lg"
                />
                <label className="form-label" htmlFor="form3Example4cg">
                  Password
                </label>
              </div>
              <div className="form-outline mb-4">
                <input
                  type="password"
                  id="form3Example4cdg"
                  className="form-control form-control-lg"
                />
                <label className="form-label" htmlFor="form3Example4cdg">
                  Repeat your password
                </label>
              </div>
              <div className="form-check d-flex justify-content-center mb-5">
                <input
                  className="form-check-input me-2"
                  type="checkbox"
                  defaultValue=""
                  id="form2Example3cg"
                />
                <label className="form-check-label" htmlFor="form2Example3g">
                  I agree all statements in{" "}
                  <a href="#!" className="text-body">
                    <u>Terms of service</u>
                  </a>
                </label>
              </div>
              <div className="d-flex justify-content-center">
                <button
                  type="button"
                  className="btn btn-success btn-block btn-lg gradient-custom-4 text-body"
                >
                  Register
                </button>
              </div>
              <p className="text-center text-muted mt-5 mb-0">
                Have already an account?{" "}
                <a href="#!" className="fw-bold text-body">
                  <u>Login here</u>
                </a>
              </p>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
</section>
    </div>



  );
};

export default Signup;
