                return (
                  <option key={i + 1} value={i + 1}>
                    {i + 1}
                  </option>
                );
              })}
            </select>
            <select className="m-2 h-100 bg-success rounded">
              <option value="1kg">1kg</option>
              <option value="5kg">5kg</option>
            </select>
            <div className="d-inline h-100 fs-5">Total Price</div>
          </div>
              </div>
