const {createContext, useState, useContext} = require('react');

//initialize the context
const UserContext=createContext();

//create a provider
const UserProider =({children}) => {

    //get user data from session and convert to JS using 'JSON.parse()'
    //and store in state
    const [currentUser, setCurrentUser] = useStatete(
        JSON.parse(sessionStorage.getItem('user'))
    )
    const [loggedIn, setLoggedIn] = useState(currentUser !==null ? true : false);

    const logout =() => {
        sessionStorage.removeItem('user');
        setCurrentUser(null);
        setLoggedIn(false);
    }

    return <UserContext.Provider value={{loggedIn, setLoggedIn}}>
        {children}
    </UserContext.Provider>
};

const useUserContext =() => useContext(UserContext);

export default useUserContext;
